import React, { Component } from "react";
import { createMuiTheme } from "@material-ui/core/styles";
import { withStyles } from '@material-ui/styles';
import Typography from "@material-ui/core/Typography";

import Fade from "@material-ui/core/Fade";
import Paper from "@material-ui/core/Paper";
import Popper from "@material-ui/core/Popper";
import { makeStyles } from '@material-ui/core/styles';

import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';



const theme = createMuiTheme({
    spacing: 4
});

const styles = {
    paper: {
        border: '1px solid',
        padding: theme.spacing(1),
        backgroundColor: theme.palette.background.paper,

    },
    paper: {
        border: '1px solid',
        padding: theme.spacing(1),
        backgroundColor: theme.palette.background.paper,

    },
    card: {
        minWidth: 275,
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
};
class SimplePopper extends Component {

    constructor(props) {
        super(props)
        this.state =
            {
                showPopper : false,
                anchorEl: null, open: false,
                paperanchorEl: null, paperopen: false
            }
        this.profileRef = React.createRef();
    }

    flipOpen = () => this.setState({ ...this.state, open: !this.state.open });
    paperflipOpen = () => this.setState({ ...this.state, paperopen: !this.state.paperopen });

    handleClick = event => {
        // this.setState({setAnchorEl: event.currentTarget }); 

        this.state.anchorEl
            ? this.setState({ anchorEl: null })
            : this.setState({ anchorEl: event.currentTarget });
        this.flipOpen();
    }

    paperhandleClick = event => {
        // this.setState({setAnchorEl: event.currentTarget }); 

        this.state.paperanchorEl
            ? this.setState({ paperanchorEl: null })
            : this.setState({ paperanchorEl: event.currentTarget });
        this.paperflipOpen();
    }


    handleClose = () => {

        this.setState({ open: null });
        //this.state.setAnchorEl = null
    }

    paperhandleClose = () => {

        this.setState({ paperopen: null });
        //this.state.setAnchorEl = null
    }


    render() {
        // const open = this.state.anchorEl === null ? false : true;
        // const id = this.state.open ? "simple-popper" : null;
        // console.log(this.state.anchorEl);
        // console.log(this.state.open);

        // const paperopen = this.state.paperanchorEl === null ? false : true;
        // const paperId = this.state.paperopen ? "simpe-popper" : null;
        // console.log(this.state.paperanchorEl);
        // console.log(this.state.paperopen);

        const { classes } = this.props;
        const bull = <span className={classes.bullet}>•</span>;

        return (
            <div>





                <div >

                        <Button
                            aria-describedby={paperId}
                            variant="contained" color="primary"
                            onClick={()=>this.setState({showPopper:!this.state.showPopper})}
                            ref = {this.profileRef}
                        >
                            Toggle Popper
            </Button>

                    <Popper
                        style={{ backgroundColor: 'lightyellow' }}
                        id={paperId}
                        open={this.state.showPopper}
                        anchorEl={this.profileRef.current}
                        onClose={this.paperhandleClose}

                    >
                        <div > <Card className={classes.card}>
                            <CardContent>
                                <Typography className={classes.title} color="textSecondary" gutterBottom>
                                    Word of the Day
        </Typography>
                                <Typography variant="h5" component="h2">
                                    be{bull}nev{bull}o{bull}lent
        </Typography>
                                <Typography className={classes.pos} color="textSecondary">
                                    adjective
        </Typography>
                                <Typography variant="body2" component="p">
                                    well meaning and kindly.
          <br />
                                    {'"a benevolent smile"'}
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <Button size="small">Learn More</Button>
                            </CardActions>
                        </Card>
                        </div>
                    </Popper>





                </div>
            </div>
        );
    }
}

export default withStyles(styles)(SimplePopper);
