import React from 'react'
import List from './List'
import data from '../sampleData'
import '.../';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';

class Board extends React.Component {
    state = {
        currentLists:[]
    }
    componentDidMount(){
        this.setState({currentLists:data.lists})

    }

    addBoardInput = React.createRef()

    createNewList = (e) =>{
        e.preventDefault()

        const list = {
            id:Math.random(),
            title:this.addBoardInput.current.value,
            board:300,
            createdAt:new Date(),
            cards:[
                {
                    id:1,
                    text:'Card 1'
                },
                {
                    id:2,
                    text:'Card 2'
                }
            ]


        }
        if(list.title){
            this.setState({currentLists:[...this.state.currentLists,list]})
        }
        this.addBoardInput.current.value=''
    }




    render() {
        return (
            <div>
            <div className="lists-wrapper">

            {Object.keys(this.state.currentLists).map(key => (
                <List key={this.state.currentLists[key].id}
                list={this.state.currentLists[key]}
                />
            ))}

            </div>
                <ClickAwayListener onClickAway={this.createNewList} className="new-list-wrapper">
            <input
             ref={this.addBoardInput}
            type="text"
            name="name"
            placeholder="+ New List"
            />

                </ClickAwayListener>
            </div>
        )
    }

}


export default Board
