import React from 'react';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import EmojiObjectsIcon from '@material-ui/icons/EmojiObjects';
import clsx from 'clsx';
import NotesIcon from '@material-ui/icons/Notes';
import Profile from './Profile'
import TextField from '@material-ui/core/TextField';
import DnsOutlinedIcon from '@material-ui/icons/DnsOutlined';
import SettingsOutlinedIcon from '@material-ui/icons/SettingsOutlined';
import AddAlertIcon from '@material-ui/icons/AddAlert';
import Badge from '@material-ui/core/Badge';
import ArchiveIcon from '@material-ui/icons/Archive';
import InputBase from '@material-ui/core/InputBase';
import SearchIcon from '@material-ui/icons/Search';
import { fade,  } from '@material-ui/core/styles';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import RefreshOutlinedIcon from '@material-ui/icons/RefreshOutlined';
import ClickAwayforNotes from './clickAwayforNotes';
import Setting from './Settings'

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
    sectionDesktop: {
        display: 'none',
        [theme.breakpoints.up('md')]: {
            display: 'flex',
        },
    },

    inputInput: {
        padding: theme.spacing(1, 1, 1, 7),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('md')]: {
            width: 200,
        },
    },

    root: {
        display: 'flex',
    },

    grow: {
        flexGrow: 1,
    },

    appBar: {

        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,            
            backgroundColor: fade(theme.palette.common.white, 0.15),            
        }),

    },

    appBarShift: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },

    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,

        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginRight: theme.spacing(2),
        marginLeft: 0,
        width: 100,
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(3),
            width: 600,
            backgroundColor: 'lightGrey' 
        },
    },

    searchIcon: {
        width: theme.spacing(7),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },

    menuButton: {
        marginRight: theme.spacing(2),
    },

    hide: {
        display: 'none',
    },

    setting:{ 
        '& .MuiPaper-elevation16': {
            boxShadow:  'none',
        },
         marginRight: 80,
         width:60
        },

    List: {
        marginRight: 0,
    },

    keepImage:{
        backgroundImage: "url('https://www.gstatic.com/images/branding/product/2x/keep_48dp.png')",
       height:'48px',
       width:'48px',

    },

    Refresh: {
        marginRight: 0,
    },

    drawer: {
        width: drawerWidth,
        flexShrink: 0,

        '& .MuiDrawer-paper': {
            top: '3.5em',
            [theme.breakpoints.up('sm')]: {
                top: '65px',
            },
        },
        '& .MuiPaper-elevation16': {
            boxShadow: 'none',
        },
        '& .MuiBackdrop-root': {
            backgroundColor: 'rgba(0, 0, 0, 0)',
        },
    },

    drawerPaper: {
        width: drawerWidth,
    },

    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },

    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        marginLeft: -drawerWidth,
    },

    contentShift: {
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
        marginLeft: 0,
    },
}));

export default function PersistentDrawerLeft() {
    const classes = useStyles();

    const [open, setOpen] = React.useState(false);

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    const [anchorEl, setAnchorEl] = React.useState(null);

    const [settingEl, setSettingsEl] = React.useState(null);

    const [, setMobileMoreAnchorEl] = React.useState(null);
    //-- for color change
    const [coloranchorEl, setcoloranchorEl] = React.useState('Notes');

    const isSettingOpen = Boolean(settingEl);

    const handleSettingMenuOpen = event => {
        setSettingsEl(event.currentTarget);
    };

    const handleMobileMenuClose = () => {
        setMobileMoreAnchorEl(null);
    };


    //for settings start 
    const handleSettingMenuClose = () => {
        setMobileMoreAnchorEl(null);
    };

    const handleSetMenuClose = () => {
        setSettingsEl(null);
        handleSettingMenuClose();
    };

    //for setting color
    const handlecoloranchorEl = (textcolor) => {
        setcoloranchorEl(textcolor);

    };

    return (
        <div className={classes.root}>
            <CssBaseline />
            <AppBar
                position="fixed"
                className={classes.appBar}
                color='inherit'
            >
                <Toolbar>
                    <IconButton
                        color="inherit"
                        aria-label="open drawer"
                        onClick={!open ? handleDrawerOpen : handleDrawerClose}
                        edge="start"
                    //className={clsx(classes.menuButton, open && classes.hide)}
                    >
                <MenuIcon />
                    </IconButton>
                    <Badge>
                        <EmojiObjectsIcon style={{ backgroundColor:'#feefc3',borderRadius:4}}/></Badge>
                    <Typography variant="h6" noWrap>
                        Keep
                    </Typography>
                    <div className={classes.search} >
                    <div className={classes.searchIcon}>
                            <SearchIcon />
                    </div>
                        <InputBase
                            placeholder="Search…"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </div>
          <div className={classes.grow} />
                    <div className={classes.sectionDesktop}>

                            <Badge color="secondary">
                                <IconButton className={classes.Refresh}><RefreshOutlinedIcon/></IconButton>
                            </Badge>


                            <Badge  color="secondary">
                                <IconButton className={classes.List}><DnsOutlinedIcon/></IconButton>
                            </Badge>



                            <Badge color="secondary">
                                <IconButton color="inherit" className={classes.setting}> <Setting /> </IconButton>
                            </Badge>

                        <IconButton>
