import React from 'react';
import './App.css';
import Board from './components/Board'
import {AuthProvider} from './components/AuthContext'
import { BrowserRouter, Route, Switch } from 'react-router-dom'
import PageNotFound from './components/Pages/PageNotFound'
import usersignup from './components/User_signup_form'
import usersignin from './components/User_signin'
import Dashboard from './components/Dashboard'
import popover from './components/popover'
import Persistent from './components/Persistent'
import mainNote from './components/mainNote'
import clickAwayforNotes from './components/clickAwayforNotes'
import toggleNote from './components/toggleNote'
import data from './sampleData'
import MMainNote from './components/MMainnote'
import Appbar from './components/Appbar'




class App extends React.Component{
  state = {
    boards: []
  }

  componentDidMount() {
    this.setState({ boards: data.boards })
    //boardsRef.add({name:'hello'})
  }

  createNewBoard = async board => {
    try {
      const newBoard = await boardsRef.add({ board })
      const boardObj = {
        id: newBoard.id,
        ...board
      }
      this.setState({ boards: [...this.state.boards, boardObj] })
    }
    catch (error) {
      console.error('Error creating new board: ', error)
    }
  }



  render(){return (
    <div className="App">

      <BrowserRouter >
      <AuthProvider>

          <Switch>
            <Route exact path="/Appbar" component={Appbar} />
            <Route exact path="/Board" component={Board} />
            <Route exact path="/MMainNote" component={MMainNote} />
            <Route exact path="/toggleNode" component={toggleNote} />
            <Route exact path="/mainNote" component={mainNote} />
            <Route exact path="/Dashboard" component={Dashboard} />
            <Route exact path="/" component={PageNotFound} />
            <Route path="/Signup" component={usersignup} />
            <Route path="/Signin" component={usersignin} />
            <Route path="/clickAwayforNotes" component={clickAwayforNotes} />
          </Switch>

        </AuthProvider>
      </BrowserRouter>

    </div>
  );}
}

export default App;
