import React, { Component } from "react";
import { createMuiTheme } from "@material-ui/core/styles";
import { withStyles } from '@material-ui/styles';
import Typography from "@material-ui/core/Typography";

import Fade from "@material-ui/core/Fade";
import Paper from "@material-ui/core/Paper";
import Popper from "@material-ui/core/Popper";
import { makeStyles } from '@material-ui/core/styles';

import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';



const theme = createMuiTheme({
    spacing: 4
});

const styles = {
    paper: {
        border: '1px solid',
        padding: theme.spacing(1),
        backgroundColor: theme.palette.background.paper,

    },
    paper: {
        border: '1px solid',
        padding: theme.spacing(1),
        backgroundColor: theme.palette.background.paper,

    },
};
class SimplePopper extends Component {

    constructor(props) {
        super(props)
        this.state =
            {
                anchorEl: null, open: false,
                paperanchorEl: null, paperopen: false
            }
    }

    flipOpen = () => this.setState({ ...this.state, open: !this.state.open });
    paperflipOpen = () => this.setState({ ...this.state, paperopen: !this.state.paperopen });

    handleClick = event => {
        // this.setState({setAnchorEl: event.currentTarget }); 

        this.state.anchorEl
            ? this.setState({ anchorEl: null })
            : this.setState({ anchorEl: event.currentTarget });
        this.flipOpen();
    }

    paperhandleClick = event => {
        // this.setState({setAnchorEl: event.currentTarget }); 

        this.state.paperanchorEl
            ? this.setState({ paperanchorEl: null })
            : this.setState({ paperanchorEl: event.currentTarget });
        this.paperflipOpen();
    }


    handleClose = () => {

        this.setState({ open: null });
        //this.state.setAnchorEl = null
    }

    paperhandleClose = () => {

        this.setState({ paperopen: null });
        //this.state.setAnchorEl = null
    }


    render() {
        const open = this.state.anchorEl === null ? false : true;
        const id = this.state.open ? "simple-popper" : null;
        console.log(this.state.anchorEl);
        console.log(this.state.open);

        const paperopen = this.state.paperanchorEl === null ? false : true;
        const paperId = this.state.paperopen ? "simpe-popper" : null;
        console.log(this.state.paperanchorEl);
        console.log(this.state.paperopen);

        const { classes } = this.props;

        return (
            <div>





                <div >

                        <Button
                            aria-describedby={paperId}
                            variant="contained" color="primary"
                            onClick={event => this.paperhandleClick(event)}

                        >
                            Toggle Popper
            </Button>

                    <Popper
                        style={{ marginTop: 60, backgroundColor: 'lightyellow',marginLeft:1450 }}
                        id={paperId}
                        open={this.state.paperopen}
                        anchorEl={this.state.paperanchorEl}
                        onClose={this.paperhandleClose}
                        anchororigin={{
                            vertical:'bottom',
                            horizontal:'center',
                        }}
                        transformorigin={{
                            vertical: 'top',
                            horizontal: 'center',
                        }}
                    >
                        <div >
                            <Typography color='inherit' className={classes.paper}>The ntent of the Popover.</Typography>
                            <Typography color='inherit' className={classes.paper}>The ntent of the Popover.</Typography>
                        </div>
                    </Popper>





                </div>
            </div>
        );
    }
}

export default withStyles(styles)(SimplePopper);
